# 用户模型
class User(db.Model):
    __tablename__ = 'user'  # 显式指定表名

    account = db.Column(db.String(50), primary_key=True, unique=True, nullable=False)
    password = db.Column(db.String(50), nullable=False)


class Music(db.Model):
    __tablename__ = 'listl'  # 显式指定表名

    id = db.Column(db.Integer, primary_key=True)
    count = db.Column(db.String(50))
    type = db.Column(db.String(50))
    mid = db.Column(db.String(100))
    name = db.Column(db.String(100))
    artist = db.Column(db.String(200))
    album = db.Column(db.String(100))
    img2 = db.Column(db.String(255))
    media_mid = db.Column(db.String(100))
    color = db.Column(db.String(20))

    def __repr__(self):
        return f'<Music {self.name}>'

class Song(db.Model):
    __tablename__ = 'songlist'  # 显式指定表名

    pri_id = db.Column(db.Integer, primary_key=True)
