@app.route('/savelist/', methods=['GET', 'POST'])
def savelist_():
    # 将传上来的数据传入数据库
    dicdata = request.get_json()  # 就是列表

    # print(dicdata)
    # 提取 play_list 和 user
    dic = dicdata.get('play_list')
    user = dicdata.get('user')
    # 修改某个用户的数据信息的时候先删除之前其保存的，简化的代码，但是有效
    try:
        db.session.query(Music).filter(
            Music.count == user).delete()  # 删除所有数据  用户名指定的
        db.session.commit()  # 提交事务
        print("数据已成功清空")
    except Exception as e:
        db.session.rollback()  # 如果出现错误，回滚事务
        print("清空数据时发生错误")
        print(str(e))

    counter = row_count = db.session.query(
        func.count(Music.id)).scalar()  # 计算 id 列的行数

    if counter > 0:
        counter = db.session.query(func.max(Music.id)).scalar()  # 获取 id 列的最大值

    # res = savelist.main(dic)
    # 创建 Music 实例
    music = []
    print(dic)
    for data in dic:
        # print(counter)
        counter = counter + 1
        # print(data)
        # 创建一个新的 Music 对象并将其添加到 music 列表中
        music.append(
            Music(
                id=counter,
                count=user,
                type=data.get('type', 'music'),  # 如果没有 'type' 键，则使用空字符串
                mid=data.get('mid', ''),  # 如果没有 'mid' 键，则使用空字符串
                name=data.get('name', ''),  # 如果没有 'name' 键，则使用空字符串
                # 如果没有 'artist' 键，则使用空列表，'.join' 将列表转为字符串
                artist=', '.join(data.get('artist', [])),
                # 如果没有 'album' 或 'name' 键，则使用空字符串
                album=data.get('album', {}).get('name', ''),
                img2=data.get('img2', ''),  # 如果没有 'img2' 键，则使用空字符串
                # 如果没有 'media_mid' 键，则使用空字符串
                media_mid=data.get('media_mid', ''),
                color=data.get('color', '')  # 如果没有 'color' 键，则使用空字符串
            )
        )

    # 保存到数据库
    try:
        db.session.bulk_save_objects(music)  # 批量保存
        db.session.commit()  # 提交事务
        print("数据上传成功")
        data = {
        "status": "成功上传！"
        }
    except Exception as e:
        db.session.rollback()  # 出错时回滚
        print(f"数据上传失败: {e}")
        data = {
            "status": "上传失败！"
        }

    # 将字典转换为 JSON 字符串
    return json.dumps(data, ensure_ascii=False)


