@app.route('/login/', methods=['POST'])
def login():
    print("login")
    code=100
    information = '登录成功'
    # 从请求体中获取用户名和密码
    account = request.json.get('account')
    password = request.json.get('password')

    # 在数据库中查找该用户名的用户
    user = User.query.filter_by(account=account).first()

    # 如果没有找到用户，或者密码不匹配，返回错误
    if user is None:
        code=200
        information = '登录失败，用户名不存在'
        data = {
            "code":code,
            "status": information
        }
        # 将字典转换为 JSON 字符串
        return json.dumps(data, ensure_ascii=False)
    if user.password != password:
        code =300
        information = '登录失败，秘密错误'
        data = {
            "code":code,
            "status": information
        }
        # 将字典转换为 JSON 字符串
        return json.dumps(data, ensure_ascii=False)
    # 如果验证成功，返回成功的消息
    data = {
        "code":code,
        "status": information
    }
    # 将字典转换为 JSON 字符串
    return json.dumps(data, ensure_ascii=False)

