#返回对应 id的歌单
def returnsonglist(i):
    songs = Song.query.filter_by(
        id=i).all()  # 查询 user 为 1 的所有记录
    # 转换数据为原始格式
    song_list = []
    for song in songs:
        # 将 artist 字段通过逗号分隔开
        artist_list = song.artist.split(', ') if song.artist else []

        # 构造回原先的数据结构
        song_data = {
            'pri_id': song.pri_id,
            'topid': song.id,
            'type': song.type if song.type else '',
            'mid': song.mid if song.mid else '',
            'name': song.name if song.name else '',
            'artist': artist_list,  # 将 artist 字符串转换回列表
            'album': {'name': song.album} if song.album else {},
            'img': song.img if song.img else '',
            'img2': song.img2 if song.img2 else '',
            'media_mid': song.media_mid if song.media_mid else '',
            'src': song.src if song.src else '',
            'lrc': song.lrc if song.lrc else '',
        }
        song_list.append(song_data)

    return song_list

