# 保存对应 id的歌单
def savesonglist(res):
    try:
        db.session.query(Song).filter(
            Song.id == res['topid']).delete()  # 删除所有数据  用户名指定的
        db.session.commit()  # 提交事务
        print("数据已成功清空")
        print(res['topid'])
    except Exception as e:
        db.session.rollback()  # 如果出现错误，回滚事务
        print("清空数据时发生错误")
        print(str(e))
    #counter = db.session.query(func.max(Song.pri_id)).scalar()
    counter = row_count = db.session.query(
        func.count(Song.pri_id)).scalar()  # 计算 id 列的行数

    if counter > 0:
        counter = db.session.query(func.max(Music.id)).scalar()  # 获取 id 列的最大值

    # 创建实例
    song = []

    for data in res['songs']:
        # print(counter)
        counter = counter + 1
        # print(data)
        # 创建一个新的 Music 对象并将其添加到 music 列表中
        song.append(
            Song(
                pri_id=counter,
                id=res['topid'],
                type=data.get('type', ''),  # 如果没有 'type' 键，则使用空字符串
                mid=data.get('mid', ''),  # 如果没有 'mid' 键，则使用空字符串
                name=data.get('name', ''),  # 如果没有 'name' 键，则使用空字符串
                # 如果没有 'artist' 键，则使用空列表，'.join' 将列表转为字符串
                artist=', '.join(data.get('artist', [])),
                # 如果没有 'album' 或 'name' 键，则使用空字符串
                album=data.get('album', {}).get('name', ''),
                img=data.get('img', ''),  # 如果没有 'img2' 键，则使用空字符串
                img2=data.get('img2', ''),  # 如果没有 'img2' 键，则使用空字符串
                # 如果没有 'media_mid' 键，则使用空字符串
                media_mid=data.get('media_mid', ''),
                src=data.get('src', ''),
                lrc=data.get('lrc', ''),
            )
        )

    # 保存到数据库
    db.session.bulk_save_objects(song)  # bulk_save_objects 用于批量保存对象
    db.session.commit()


